&lt;template>
  &lt;div>
    &lt;JqxTooltip ref="myTooltip"
          :content="'This is a jqxButton'" :position="'bottom'" :autoHide="false">
      &lt;JqxButton :width="500">Hover to see the tooltip&lt;/JqxButton>
    &lt;/JqxTooltip>
    &lt;JqxButton style="margin-top: 20px" :width="80" @click="close()">Close&lt;/JqxButton>
  &lt;/div>
&lt;/template>

&lt;script>
  import JqxTooltip from 'jqwidgets-scripts/jqwidgets-vue/vue_jqxtooltip.vue';
  import JqxButton from 'jqwidgets-scripts/jqwidgets-vue/vue_jqxbuttons.vue';

  export default {
    components: {
      JqxTooltip,
      JqxButton
    },
    methods: {
      close: function () {
        this.$refs.myTooltip.close();
      }    
    }
  }
&lt;/script>
