customExample.code:
&lt;template>
  &lt;JqxTreeGrid ref="myTreeGrid"
  :source="source" :columns="columns" :icons="true" />
&lt;/template>

&lt;script>
  import JqxTreeGrid from 'jqwidgets-scripts/jqwidgets-vue/vue_jqxtreegrid.vue';

  export default {
    components: {
      JqxTreeGrid
    },
    data: function () {
      return {
        source: new $.jqx.dataAdapter(this.data, {
          beforeLoadComplete: (records) => {
            this.addIcons(records[0]);
            return records;
          }
        }),
        columns: [
          { text: 'FirstName', dataField: 'FirstName', width: 150, aggregates: ['count'] },
          { text: 'LastName', dataField: 'LastName', width: 120 },
          { text: 'Title', dataField: 'Title', width: 200 }
        ]
      }
    },
    beforeCreate: function () {
      let employees = [{
        'EmployeeID': 2,
        'FirstName': 'Andrew',
        'LastName': 'Fuller',
        'Title': 'Vice President, Sales',
        'expanded': 'true',
        children: [{
          'EmployeeID': 8,
          'FirstName': 'Laura',
          'LastName': 'Callahan',
          'Title': 'Inside Sales Coordinator'
        }, {
          'EmployeeID': 5,
          'FirstName': 'Steven',
          'LastName': 'Buchanan',
          'Title': 'Sales Manager',
          'expanded': 'true',
          children: [{
            'EmployeeID': 6,
            'FirstName': 'Michael',
            'LastName': 'Suyama',
            'Title': 'Sales Representative'
          }, {
            'EmployeeID': 7,
            'FirstName': 'Robert',
            'LastName': 'King',
            'Title': 'Sales Representative'
          }]
        }]
      }];
      this.data = {
        dataType: "json",
        dataFields: [
          { name: 'EmployeeID', type: 'number' },
          { name: 'FirstName', type: 'string' },
          { name: 'LastName', type: 'string' },
          { name: 'Title', type: 'string' },
          { name: 'children', type: 'array' },
          { name: 'expanded', type: 'bool' }
        ],
        hierarchy: { root: 'children' },
        id: 'EmployeeID',
        localData: employees
      };
    },
    methods: {
      addIcons: function (record) {
        var imgUrl = "https://cdn2.iconfinder.com/data/icons/flat-ui-icons-24-px/24/man-24-16.png";
        record.icon = imgUrl;
        let children = record.children;
        if (children) {
          for (let i = 0; i &lt; children.length; i++) {
            this.addIcons(children[i]);
          }
        }
      }
    }
  }
&lt;/script>

